import React from 'react';
import Note from './Note';


class Noteboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          buttoncount: 0,
          clean: false
        };
    }

    toggleClass(i) {
        const count = this.state.buttoncount;
        const selectedone = i.target;
        if (count < 4){
          var newcount = this.state.buttoncount + 1;
          this.setState({
            buttoncount: newcount,
          });
          selectedone.classList.toggle("toggled");
        } else {
          var cleancount = 0;
          this.setState({
            buttoncount: cleancount,
          });
        }
        console.log(i.target);
        this.props.onClick(i);
    };



    render() {
        const allnotes = this.props.notes;
        const listItems = allnotes.map((note) =>
            <Note
                value={note}
                key={note}
                note={note}
                onClick={(i) => this.toggleClass(i)}
                active={this.state.clean}
                >{note}</Note>
            );
        return (
          <div>
            <div className="noteboard">{listItems}
            </div>
            <div>{this.state.buttoncount}</div>
          </div>
          );
        }
    }

export default Noteboard;
