import Note from './Note';


class Noteboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [{ id: 1, text: "item 1" }, { id: 2, text: "item 2" }],
            favorites: []
        };
        this.clearAll = this.clearAll.bind(this);

    }

    // toggleClass(i) {
