        const { items, favorites } = this.state;
        const { addFavorite } = this;


        // const listItems = allnotes.map((note) =>
        //     <Note
        //         value={note}
        //         key={note}
        //         note={note}
        //         onClick={(i) => this.toggleClass(i)}
        //         // className={ this.state.active ? "noteboardbtn toggled" : "noteboardbtn" }
        //         active={this.state.active}

        //         >{note}</Note>
        //     );
        return (
            <div>
                <Results
                items={items}
                favorites={favorites}
                addFavorite={addFavorite}
                />
                <button onClick={this.clearAll}>clear</button>
            </div>
          );
        }
    }


    class Results extends React.Component {
        render() {
          const { items, favorites, addFavorite } = this.props;

          return (
            <div>
              {items.map(item => (
                <div
                  key={item.id}
                  onClick={() => {
                    addFavorite(item);
                  }}
                >
                  {favorites.includes(item) && 'Favorite: '}
                  {item.text}
                </div>
              ))}
            </div>
          );
        }
      }

export default Noteboard;
