    //       }
    //     }
    //     console.log(i.target,"iiii",allnotes);
        this.props.onClick(note);
    };



    render() {
        const allnotes = this.props.notes;
        const listItems = allnotes.map((note) =>
            <Note
                value={note.value}
                key={note.id}
                note={note}
                onClick={() => this.toggleClass(note)}
                styling={note.styling}
                >{note}</Note>
            );
        return (
          <div>
            <div className="noteboard">{listItems}
            </div>
          </div>
          );
        }
    }

export default Noteboard;
