import React from 'react';
import Note from './Note';


class Noteboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
    }

    toggleClass(i) {
        const currentState = this.state.active;
        this.setState({ active: !currentState });
        this.props.onClick(i);
        console.log(i.id);
    };



    render() {
        const allnotes = this.props.notes;
        const listItems = allnotes.map((note) =>
            <Note
                value={note}
                key={note}
                note={note}
                onClick={(i) => this.toggleClass(i)}
                // className={ this.state.active ? "noteboardbtn toggled" : "noteboardbtn" }
                active={this.state.active}

                >{note}</Note>
            );
        return (
            <div className="noteboard">{listItems}
            </div>
          );
        }
    }


    class Results extends React.Component {
        render() {
          const { items, favorites, addFavorite } = this.props;

          return (
            <div>
              {items.map(item => (
                <div
                  key={item.id}
                  onClick={() => {
                    addFavorite(item);
                  }}
                >
                  {favorites.includes(item) && 'Favorite: '}
                  {item.text}
                </div>
              ))}
            </div>
          );
        }
      }

export default Noteboard;
