import React from 'react';


class Note extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
    }

    toggleClass(i) {
        const currentState = this.state.active;
        this.setState({ active: !currentState });
    };



    render() {
        return (
            <button className={ this.state.active ? "noteboardbtn toggled" : "noteboardbtn" }>{this.props.note}
            </button>
          );
        }
    }
    export default Note;
